export class ChangeGroup {
  constructor(title, affectedItems) {
    this.title = title;
    this.affectedItems = affectedItems;
  }

  commit() {}

  abort() {}

  mergeWith(other) {
    return false;
  }

  execute(item) {
    item.do();

    for (let existingItem of this.items) {
      if (existingItem.mergeWith(item)) return;
    }

    this.items.push(item);
  }

}