import { createStore } from '@stencil/store';
import { en } from '../assets/i18n/en.js';
const { state, onChange } = createStore(en);
const esI18n = async () => {
  const { es } = await import(`../assets/i18n/es`);
  return es;
};
const deI18n = async () => {
  const { de } = await import(`../assets/i18n/de`);
  return de;
};
const nlI18n = async () => {
  const { nl } = await import(`../assets/i18n/nl`);
  return nl;
};
const enI18n = () => en;
onChange('lang', async (lang) => {
  let bundle;
  switch (lang) {
    case 'es':
      bundle = await esI18n();
      break;
    case 'de':
      bundle = await deI18n();
      break;
    case 'nl':
      bundle = await nlI18n();
      break;
    default:
      bundle = enI18n();
  }
  Object.assign(state, Object.assign({ custom: state.custom }, bundle));
});
export default { state };
