new RovingTabindexController<AccordionItem>(
        this,
        {
            focusInIndex: (elements: AccordionItem[]) => {
                return elements.findIndex((el) => {
                    return !el.disabled;
                });
            },
            direction: 'vertical',
            elements: () => this.items,
            isFocusableElement: (el: AccordionItem) => !el.disabled,
        }
    )

